import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../components/ProfilePage.css'; // Importez le fichier CSS pour la page profil

const ProfilePage = () => {
  const [user, setUser] = useState(null); // État pour stocker les données utilisateur
  const [loading, setLoading] = useState(true); // État pour gérer le chargement
  const [error, setError] = useState(''); // État pour gérer les erreurs
  const [newEmail, setNewEmail] = useState(''); // État pour le nouvel email
  const [newPassword, setNewPassword] = useState(''); // État pour le nouveau mot de passe
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('token'); // Récupérer le token

      try {
        const response = await axios.get('http://localhost:3001/api/profile', {
          headers: {
            'Authorization': `Bearer ${token}`, // Ajouter le token dans les en-têtes
          },
          withCredentials: true
        });
        setUser(response.data); // Stocker les données utilisateur
      } catch (err) {
        console.error('Erreur lors de la récupération des données utilisateur:', err);
        setError('Erreur lors de la récupération des données.'); // Message d'erreur
        navigate('/login'); // Rediriger vers la page de connexion en cas d'erreur
      } finally {
        setLoading(false); 
      }
    };

    fetchUserData();
  }, [navigate]);

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:3001/api/logout', {}, { withCredentials: true });
      localStorage.removeItem('token');
      localStorage.removeItem('userId');
      localStorage.removeItem('role');
      navigate('/login');
    } catch (error) {
      console.error('Erreur lors de la déconnexion:', error);
      setError('Erreur lors de la déconnexion.');
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault(); 
    const token = localStorage.getItem('token');

    try {
      const response = await axios.put('http://localhost:3001/api/profile', {
        email: newEmail, 
        password: newPassword 
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        withCredentials: true
      });

      setUser({ ...user, email: response.data.email });
      setNewEmail('');
      setNewPassword('');
      setError(''); 
    } catch (err) {
      console.error('Erreur lors de la mise à jour du profil:', err);
      setError('Erreur lors de la mise à jour des données.');
    }
  };

  if (loading) {
    return <p>Chargement...</p>;
  }

  return (
    <div className="profile-container">
      {error && <div className="alert alert-danger">{error}</div>}
      {user ? (
        <div>
          <div className="card user-info">
            <div className="card-body">
              <h5 className="card-title">Bienvenue, {user.username}</h5>
              <p className="card-text">Email: {user.email}</p>
              <p className="card-text">Statut : {user.role === 'admin' ? 'Administrateur' : 'Utilisateur'}</p>
              <button className="btn btn-danger logout-button" onClick={handleLogout}>Déconnexion</button>
            </div>
          </div>

          <h5 className="update-title">Mettre à jour le profil</h5>
          <form onSubmit={handleUpdate}>
            <div className="form-group">
              <input
                type="email"
                value={newEmail}
                onChange={(e) => setNewEmail(e.target.value)}
                placeholder="Nouvel email"
                className="form-control"
              />
            </div>
            <div className="form-group">
              <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                placeholder="Nouveau mot de passe"
                className="form-control"
              />
            </div>
            <button type="submit" className="btn btn-primary">Mettre à jour</button>
          </form>
        </div>
      ) : (
        <p>Chargement...</p> 
      )}
      <div className="actions">
        <button onClick={() => navigate('/tickets')} className="btn btn-secondary">Voir mes billets</button> 
        <button onClick={() => navigate('/mes-factures')} className="btn btn-secondary">Mes Factures</button> 
      </div>
    </div>
  );
};

export default ProfilePage;
