import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css'; 

const ResetPasswordPage = () => {
  const [email, setEmail] = useState('');
  const [newPassword, setNewPassword] = useState(''); // État pour le nouveau mot de passe
  const [phone, setPhone] = useState(''); // État pour le numéro de téléphone
  const [message, setMessage] = useState(''); // État pour les messages d'erreur ou de succès
  const navigate = useNavigate(); // Hook pour la navigation

  const handleResetPassword = async (e) => {
    e.preventDefault(); // Empêcher le rechargement de la page
    setMessage(''); // Réinitialiser le message

    try {
      const response = await axios.post('http://localhost:3001/api/reset-password', {
        email,
        newPassword,
        phone, // Inclure le numéro de téléphone dans la requête
      });

      if (response.status === 200) {
        setMessage('Mot de passe réinitialisé avec succès.'); // Message de succès
        // Optionnel : Rediriger vers la page de connexion
        navigate('/login'); // Redirection vers la page de connexion
      }
    } catch (error) {
      console.error("Erreur lors de la réinitialisation du mot de passe:", error);
      setMessage('Erreur lors de la réinitialisation du mot de passe.'); // Message d'erreur
    }
  };

  return (
    <div className="container">
      <h2>Réinitialiser le mot de passe</h2> {/* Titre de la page de réinitialisation */}
      <form onSubmit={handleResetPassword}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)} // Gérer l'email
          placeholder="Email"
          required // Champ obligatoire
        />
        <input
          type="text"
          value={phone}
          onChange={(e) => setPhone(e.target.value)} // Gérer le numéro de téléphone
          placeholder="Numéro de téléphone"
          required // Champ obligatoire
        />
        <input
          type="password"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)} // Gérer le nouveau mot de passe
          placeholder="Nouveau mot de passe"
          required // Champ obligatoire
        />
        <button type="submit">Réinitialiser le mot de passe</button> {/* Bouton pour soumettre le formulaire */}
        {message && <p className="alert">{message}</p>} {/* Afficher le message d'erreur ou de succès */}
      </form>
    </div>
  );
};

export default ResetPasswordPage;
