import React, { useState } from 'react';
import axios from 'axios';
import '../App.css';
import '../components/RegisterPage.css';

const RegisterPage = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [phone, setPhone] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Vérifier que les mots de passe correspondent
    if (password !== confirmPassword) {
      setError('Les mots de passe ne correspondent pas.'); 
      return;
    }

    try {
      const response = await axios.post('http://localhost:3001/api/register', {
        username,
        email,
        password,
        phone
      });
      setSuccess('Inscription réussie ! Vous pouvez maintenant vous connecter.'); 
    } catch (err) {
      // Gérer les erreurs lors de l'inscription
      setError(err.response?.data?.message || 'Erreur lors de l\'inscription.');
    }
  };

  return (
    <div className="container">
      <h2>Inscription</h2> 
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nom d'utilisateur :</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)} 
            required 
          />
        </div>
        <div className="form-group">
          <label>Email :</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)} 
            required // Champ obligatoire
          />
        </div>
        <div className="form-group">
          <label>Mot de passe :</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} // Gérer le mot de passe
            required // Champ obligatoire
          />
        </div>
        <div className="form-group">
          <label>Validation du mot de passe :</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)} // Gérer la validation du mot de passe
            required // Champ obligatoire
          />
        </div>
        <div className="form-group">
          <label>Téléphone :</label>
          <input
            type="text"
            value={phone}
            onChange={(e) => setPhone(e.target.value)} // Gérer le numéro de téléphone
            required // Rendre le téléphone obligatoire
          />
        </div>
        <button type="submit">S'inscrire</button> {/* Bouton pour soumettre le formulaire d'inscription */}
      </form>
      {error && <p className="alert alert-danger">{error}</p>} {/* Afficher le message d'erreur si présent */}
      {success && <p className="alert alert-success">{success}</p>} {/* Afficher le message de succès si présent */}
    </div>
  );
};

export default RegisterPage;
