import React, { useEffect, useState } from 'react';
import axios from 'axios';
import QRCodeComposant from '../components/QRCodeComponent'; // Assurez-vous que le chemin est correct
import '../components/TicketList.css'; // Importer le CSS pour styliser les billets

const TicketList = () => {
    const [tickets, setTickets] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const userId = localStorage.getItem('userId'); // Récupérer l'userId depuis le localStorage

    useEffect(() => {
        const fetchTickets = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/tickets', { withCredentials: true });
                setTickets(response.data);
            } catch (err) {
                console.error('Erreur lors de la récupération des billets:', err);
                setError('Erreur lors de la récupération des billets.');
            } finally {
                setLoading(false);
            }
        };

        fetchTickets();
    }, []);

    if (loading) {
        return <p>Chargement des billets...</p>; // Message de chargement
    }

    return (
        <div className="ticket-container">
            <h2>Mes Billets</h2>
            {error && <div className="alert alert-danger">{error}</div>} {/* Affichage de l'erreur */}
            {tickets.length > 0 ? (
                tickets.map(ticket => (
                    <div key={ticket.ticket_id} className="ticket">
                        <div className="ticket-content">
                            <div className="ticket-qr">
                                <QRCodeComposant 
                                    authKey={localStorage.getItem('auth_key')} 
                                    ticketKey={ticket.ticket_key} 
                                />
                            </div>
                            <div className="ticket-info">
                                <div className="ticket-header">
                                    <span className="ticket-amount">Montant : {ticket.purchase_amount} €</span>
                                </div>
                                <h3 className="ticket-id">{ticket.ticket_id}</h3>
                            </div>
                        </div>
                    </div>
                ))
            ) : (
                <p>Aucun billet trouvé.</p> // Message lorsque aucun billet n'est trouvé
            )}
        </div>
    );
};

export default TicketList;
